import { useState } from 'react';
import { Deck } from "./deck.js"
 
let deck = new Deck;
let drawpile = [];
let discard = [];
let dealt = Array.from({length: 7},()=> Array.from({length: 13}, () => null));

function deal() {
  let newCards = Array.from({length: 7},()=> Array.from({length: 13}, () => null));
  for (let i = 0; i < 7; i++) {
    for (let j = 0; j < i + 1; j++) {
      dealt[i][j] = deck.deal_top();
      let val = dealt[i][j].m_value.toString() + dealt[i][j].m_suit;
      newCards[i][j] = val;
    }
  }
  while (deck.m_size > 0) {
    drawpile.push(deck.deal_top());
  }
  return newCards;
}

function faceup(i, j, face) {
  return face[i][j];
}

function get_img_link(card, faceup = true, empty = false) {
  if (faceup == null) { // fix
    if (empty) {
      return "/images/discard.png";
    }
    return null;
  }
  if (!faceup) {
    return "/images/back.png";
  }
  suit = card[card.length-1];
  value = parseInt(card);
  let link = "/images/";
  switch(value) {
    case 1:
      link += "A";
      break;
    case 11:
      link += "J";
      break;
    case 12:
      link += "Q";
      break;
    case 13:
      link += "K";
      break;
    default:
      link += value.toString();
  }
  link += "of-";
  switch(suit) {
    case 'D':
      link += "Diamonds";
      break;
    case 'C':
      link += "Clubs";
      break;
    case 'H':
      link += "Hearts";
      break;
    case 'S':
      link += "Spades";
      break;
  }
  link += "-White.png";
  return link;
}

export default function Board() { // board inspired by tic tac toe tutorial
  const [cards, setCards] = useState(Array.from({length: 7},()=> Array.from({length: 13}, () => null)));
  const [face, setFace] = useState(Array.from({length: 7},()=> Array.from({length: 13}, () => null)));
  const [piles, setPiles] = useState(Array.from({length: 4},()=> Array.from({length: 13}, () => null)));
  const [start, setStart] = useState(true);
    if (start) { // shuffle and deal
      deck.shuffle();
      let newCardValue = deal();
      console.log(newCardValue);
      setCards(newCardValue);
      let newFace = face;
      for (let i = 0; i < 7; i++) {
        for (let j = 0; j < i + 1; j++) {
          if (i == j) {
            newFace[i][j] = true;
          }
          else {
            newFace[i][j] = false;
          }
        }
      }
      setFace(newFace);
      setStart(false);
    }

    function restart() {
      setCards(Array.from({length: 7},()=> Array.from({length: 13}, () => null)));
      drawpile.reset();
      setFace(Array.from({length: 7},()=> Array.from({length: 13}, () => null)));
      setPiles(Array.from({length: 4},()=> Array.from({length: 13}, () => null)));
      setStart(true);
    }

    document.onkeydown = (e) => { // control what happens when keys are hit
      let dir;
      if (e.code === "ArrowUp" || e.code === "KeyW") {
        console.log("up pressed");
        dir = UP;
      } 
      else if (e.code === "ArrowDown" || e.code === "KeyS") {
        console.log("down pressed");
        dir = DOWN;
      } 
      else if (e.code === "ArrowLeft" || e.code === "KeyA") {
        console.log("left pressed");
        dir = LEFT;
      } 
      else if (e.code === "ArrowRight" || e.code === "KeyD") {
        console.log("right pressed");
        dir = RIGHT;
      }
      else {
        return;
      }
      // let nextSquares = move(squares, dir);
      // if (nextSquares != null) {
      //   const [i, j] = getRandomOpenSquare(nextSquares);
      //   if (i != null && j != null) {
      //     nextSquares[i][j] = newValue();
      //   }
      //   setSquares(nextSquares);
      // }
    };
    //let str = get_img_link("1H");
    //document.getElementById("sp").setAttribute("src", str);

    /*position: absolute;
  left:var(m_left);
  top:var(m_top);*/
    return ( // return the board object
      <>
        <div className="center-screen">
          <div className="board-row">
            <button className="card">
              <img id="sp" alt="stockpile" src="/images/back.png" style={{ width: "120px", height: "168px" }} />
            </button>
            <button className="card">{null}</button>
            <button className="card">{null}</button>
            <button className="card">{null}</button>
            <button className="card">{piles[0][0]}</button>
            <button className="card">{piles[1][0]}</button>
            <button className="card">{piles[2][0]}</button>
            <button className="card">{piles[3][0]}</button>
          </div>
        </div>
        <div className="center-screen">
          <div className="board-row">
            <button id="tableau1bottom" className="card" style={{ left: "20px", top: "210px" }}>
              <img id="t1b" src={get_img_link("1H", true)} alt="card" style={{ width: "120px", height: "168px" }} />
            </button>
            <button id="tableau2bottom" className="card" style={{ left: "160px", top: "210px" }}>
              <img id="t2b" src="/images/back.png" alt="card" style={{ width: "120px", height: "168px" }} />
            </button>
            <button id="tableau3bottom" className="card" style={{ left: "300px", top: "210px" }}>
              <img id="t2b" src="/images/back.png" alt="card" style={{ width: "120px", height: "168px" }} />
            </button>
            <button id="tableau4bottom" className="card" style={{ left: "440px", top: "210px" }}>
              <img id="t2b" src="/images/back.png" alt="card" style={{ width: "120px", height: "168px" }} />
            </button>
            <button id="tableau5bottom" className="card" style={{ left: "580px", top: "210px" }}>
              <img id="t2b" src="/images/back.png" alt="card" style={{ width: "120px", height: "168px" }} />
            </button>
            <button id="tableau6bottom" className="card" style={{ left: "720px", top: "210px" }}>
              <img id="t2b" src="/images/back.png" alt="card" style={{ width: "120px", height: "168px" }} />
            </button>
            <button id="tableau7bottom" className="card" style={{ left: "860px", top: "210px" }}>
              <img id="t2b" src="/images/back.png" alt="card" style={{ width: "120px", height: "168px" }} />
            </button>
            <button className="card">{cards[0][0]}</button>
            <button className="card">{cards[1][0]}</button>
            <button className="card">{cards[2][0]}</button>
            <button className="card">{cards[3][0]}</button>
            <button className="card">{cards[4][0]}</button>
            <button className="card">{cards[5][0]}</button>
            <button className="card">{cards[6][0]}</button>
          </div>
        </div>
        <div className="center-screen">
          <div className="board-row">
            <button className="card">{cards[0][1]}</button>
            <button className="card">{cards[1][1]}</button>
            <button className="card">{cards[2][1]}</button>
